function smoothScroll(t){$('a[href^="#"').on("click",function(o){var c=$($(this).attr("href"));c.length&&(o.preventDefault(),$("html, body").animate({scrollTop:c.offset().top},t))})}function workBelt(){$(".thumb-unit").click(function(){$(".work-belt").css("left","-100%")}),$(".work-box").click(function(){$(".work-belt").css("left","0%")})}function workLoad(){$.ajaxSetup({cache:!1}),$(".thumb-unit").click(function(){var t=$(this),o=t.find("strong").text(),c=t.data("folder"),l='<div class="loader">Loading...</div>',n="/work/"+c+".html";$(".project-load").html(l).load(n),$(".project-title").text(o)})}$(document).ready(function(){smoothScroll(300),workBelt(),workLoad()});